def step_right(art, length):
  for x in range(0,len(art)):
    if (art[x] == '\n'):
      art[x-1] = 'X'
  art[:] = [x for x in art if x != '\n']
  for x in range(0,len(art)):
    if (art[x] == 'X'):
      art[x] = '\n'
  return art
